class Solution:
    def findMaxLength(self, nums):
        counter_dict = dict()
        counter_dict[0] = -1
        max_len = 0
        counter = 0
        for i in range(len(nums)):
            counter += 1 if nums[i] == 1 else -1
            if counter_dict.get(counter) != None:
                max_len = max(max_len, i - counter_dict[counter])
            else:
                counter_dict[counter] = i
        return max_len
